mapper.select:
select u.user_id, u.dept_id, u.nick_name, u.user_name, u.job_number, u.email, u.avatar, u.phonenumber, u.password, u.sex, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time,u.update_time, u.remark, d.dept_name, d.leader,u.person_id,u.is_temporary,u.is_clinical,u.is_private,u.id_num,u.work_place,u.frequency from sys_user u left join sys_dept d on u.dept_id = d.dept_id where u.del_flag = '0'
AND u.user_name like concat('%', #{userName}, '%')
AND u.nick_name like concat('%', #{nickName}, '%')
AND u.status = #{status}
AND u.phonenumber like concat('%', #{phonenumber}, '%')
AND u.job_number = #{jobNumber}
AND date_format(u.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
AND date_format(u.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE find_in_set(#{deptId}, ancestors) ))
${params.dataScope}

select distinct u.user_id, u.dept_id, u.user_name, u.job_number, u.nick_name, u.email, u.phonenumber, u.status, u.create_time from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
${params.dataScope}

select distinct u.user_id, u.dept_id, u.user_name, u.job_number, u.nick_name, u.email, u.phonenumber, u.status, u.create_time from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and (r.role_id != #{roleId} or r.role_id IS NULL) and u.user_id not in (select u.user_id from sys_user u inner join sys_user_role ur on u.user_id = ur.user_id and ur.role_id = #{roleId})
AND u.user_name like concat('%', #{userName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
AND u.dept_id = #{deptId}
${params.dataScope}


where 1=1

	and u.nick_name = #{userName}


	and u.phonenumber=#{phone}


	and u.job_number = #{jobNumber}


where 1=1

	and u.user_name = #{userName}


	and u.phonenumber=#{phone}


	and u.id_num = #{idNum}

mapper.update:
update sys_user
dept_id = #{deptId}, user_name = #{userName}, nick_name = #{nickName}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, id_num = #{idNum}, job_number = #{jobNumber}, person_id = #{personId}, is_private = #{isPrivate}, is_clinical = #{isClinical}, is_temporary = #{isTemporary}, risk_level = #{riskLevel}, post_level = #{postLevel}, work_place = #{workPlace}, frequency = #{frequency}, is_echelon = #{isEchelon}, is_echelon is null, update_time = sysdate()
where user_id = #{userId}

mapper.select:
select u.user_id, u.dept_id, u.nick_name, u.user_name, u.job_number, u.email, u.avatar, u.phonenumber, u.password, u.sex, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time,u.update_time, u.remark, d.dept_name, d.leader,u.person_id,u.is_temporary,u.is_clinical,u.is_private,u.id_num,u.work_place,u.frequency from sys_user u left join sys_dept d on u.dept_id = d.dept_id where u.del_flag = '0' and u.status = '0'
AND u.user_name like concat('%', #{userName}, '%')
AND u.nick_name like concat('%', #{nickName}, '%')
AND u.status = #{status}
AND u.phonenumber like concat('%', #{phonenumber}, '%')
AND u.job_number = #{jobNumber}
AND date_format(u.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
AND date_format(u.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE find_in_set(#{deptId}, ancestors) ))
${params.dataScope}
